PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE "schema_migrations" ("version" varchar(255) NOT NULL);
CREATE TABLE "products" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "title" varchar(255), "image_url" varchar(255), "bh" varchar(255), "weight" decimal, "color_id" integer, "memo" text, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE "colors" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "name" varchar(255), "bh" varchar(255), "value" varchar(255), "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "memo" text);
CREATE TABLE "stocks" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "bh" varchar(255), "name" varchar(255), "color" varchar(255), "color_id" integer, "product_id" integer, "quantity" integer, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE "in_stocks" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "user_id" integer, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE "in_stock_items" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "in_stock_id" integer, "product_id" integer, "color_id" integer, "weight" decimal, "quantity" integer, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE "specs" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "product_id" integer, "bh" varchar(255), "name" varchar(255), "color_id" integer, "material" varchar(255), "quantity" integer, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "memo" text);
CREATE TABLE "spec_properties" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "name" varchar(255), "key" varchar(255), "value" text, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX "unique_schema_migrations" ON "schema_migrations" ("version");
COMMIT;
